import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import Category from './Category';
// import { persons } from './persons';


class App extends Component {
	constructor() {
		super()
		this.state = {
			persons: [],
			searchfield: ''
		}
	}


	componentDidMount() {
		// this.setState({ persons: persons })
		fetch('https://swapi.co/api/people/')
		.then(response => response.json())
		.then(users => {
			this.setState({ persons: users.results })
		})
	}

	onSearchSent = (event) => {
		if (event.key === 'Enter') {
			const search = 'https://swapi.co/api/people/?search=' + event.target.value 
			fetch(search)
			// fetch('https://swapi.co/api/people/')
			.then(response => response.json())
			.then(users => {
				this.setState({ persons: users.results })
			})			
			// const searchedPersons = 
			// this.setState({ searchfield: event.target.value });

		}
		// this.setState({ searchfield: event.target.value });
	}

	onCategorySelect = () => {
		console.log('Knappen funkar');
		// fetch(category)
		// .then(response => response.json())
		// .then(users => {
		// 	this.setState({ persons: users.results})
		// })
	}

	render() {
		if (this.state.persons.length === 0) {
			return <h1>Loading</h1>
		} else {
			return (
			    <div>
			      <h1>Star Wars</h1>
			      <SearchBox searchSent={this.onSearchSent}/>
			      <Category categorySelect={this.onCategorySelect}/>
			      <CardList persons={this.state.persons}/>
			    </div>
	  		);
		}

	}

}

export default App;
