import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import CategoryCard from './CategoryCard';
import NextPrevious from './NextPrevious';
import './App.css';

class App extends Component {
	constructor() {
		super()
		this.state = {
			starwars: [],
			searchfield: '',
			category: '',
			nexprev: "next page",
		}
	}


	componentDidMount() {
		// this.setState({ starwars: starwars })
		fetch('https://swapi.co/api/people/')
		.then(response => response.json())
		.then(users => {
			this.setState({ starwars: users.results })
			this.setState({ nexprev: users.next })
		})
		console.log(nexprev);
	}

	onSearchChange = (event) => {
		console.log(event.target.value);
		this.setState({ searchfield: event.target.value });
		if (event.key === 'Enter') {
			this.onSearchSent();
		}
	}

	onSearchSent = (event) => {
		console.log('knappen klickad');
		console.log(this.state.searchfield);
		// if (event.key === 'Enter') {
			const search = this.state.category + '?search=' + this.state.searchfield;
			fetch(search)
			.then(response => response.json())
			.then(users => {
				this.setState({ starwars: users.results })
				this.setState({ nexprev: users })
			});
	}

	onCategorySelect = (event) => {
		this.setState({ category: event.target.id });
		console.log(event.target.id);
		fetch(event.target.id)
		.then(response => response.json())
		.then(users => {
			this.setState({ starwars: users.results})
		})
	}

	render() {
		// if (this.state.starwars.length === 0) {
		// 	return <h1>Loading</h1>
		// } else {
		return (
			<div className="tc">
				<h1>Star Wars</h1>
				<SearchBox searchSent={this.onSearchSent} searchChange={this.onSearchChange} category={this.state.category}/>
				<CategoryCard categorySelect={this.onCategorySelect}/>
				<CardList starwars={this.state.starwars} category={this.state.category}/>
				<NextPrevious nexprev={this.state.nexprev}/>
			</div>
		);
	}

	// }

}

export default App;
