import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import CategoryCard from './CategoryCard';
// import { persons } from './persons';


class App extends Component {
	constructor() {
		super()
		this.state = {
			starwars: [],
			searchfield: '',
			category: 'https://swapi.co/api/starships/'
		}
	}


	componentDidMount() {
		// this.setState({ starwars: starwars })
		fetch('https://swapi.co/api/people/')
		.then(response => response.json())
		.then(users => {
			this.setState({ starwars: users.results })
		})
	}

	onSearchChange = (event) => {
		console.log(event.target.value);
		this.setState({ searchfield: event.target.value });
		if (event.key === 'Enter') {
			this.onSearchSent();
		}
	}

	onSearchSent = (event) => {
		// console.log(event.target.value);
		console.log('knappen klickad');
		console.log(this.state.searchfield);
		// if (event.key === 'Enter') {
			const search = 'https://swapi.co/api/people/?search=' + this.state.searchfield 
			fetch(search)
			// fetch('https://swapi.co/api/people/')
			.then(response => response.json())
			.then(users => {
				this.setState({ starwars: users.results })
			})			
			// const searchedstarwars = 
			// this.setState({ searchfield: event.target.value });

		// } else {
			// this.setState({ searchfield: event.target.value });
		// }
	}

	onCategorySelect = (event) => {
		this.setState({ category: event.target.id });
		console.log('Knappen funkar');
		console.log(event.target.id);
		fetch(event.target.id)
		.then(response => response.json())
		.then(users => {
			this.setState({ starwars: users.results})
		})
		// switch (event.target.id) {
		// 	case people:
		// 		fetch('https://swapi.co/api/people/')
		// 		.then(response => response.json())
		// 		.then(users => {
		// 			this.setState({ starwars: users.results})
		// 		})
		// 	break;
		// }

	}

	render() {
		if (this.state.starwars.length === 0) {
			return <h1>Loading</h1>
		} else {
			return (
			    <div>
			      <h1>Star Wars</h1>
			      <SearchBox searchSent={this.onSearchSent} searchChange={this.onSearchChange}/>
			      <CategoryCard categorySelect={this.onCategorySelect}/>
			      <CardList starwars={this.state.starwars}/>
			    </div>
	  		);
		}

	}

}

export default App;
